from __future__ import annotationsimport loggingdef configure_logging(log_level: str = "INFO") -> None:    """    Configure application logging once per process.    - Uses a simple, readable format.    - Intended to be called by CLI entrypoints and ASGI startup code.    """    level = getattr(logging, log_level.upper(), logging.INFO)    logging.basicConfig(        level=level,        format="%(asctime)s | %(levelname)s | %(name)s | %(message)s",    )