UPDATE importsSET  finished_at_utc    = :finished_at_utc,  ts_min_utc         = :ts_min_utc,  ts_max_utc         = :ts_max_utc,  row_count_read     = :row_count_read,  row_count_inserted = :row_count_inserted,  row_count_updated  = :row_count_updated,  row_count_rejected = :row_count_rejected,  status             = :status,  error_summary      = :error_summaryWHERE import_id = :import_id;